import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import './StronaWiadomosci.css';

const StronaWiadomosci = () => {
  const { user } = useContext(AuthContext);
  const [wiadomosci, setWiadomosci] = useState([]);

  useEffect(() => {
    if (user) {
      fetch('http://127.0.0.1:5000/api/wiadomosci', {
        credentials: 'include'
      })
        .then(response => response.json())
        .then(data => setWiadomosci(data.received))
        .catch(error => console.error('Error:', error));
    }
  }, [user]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const content = e.target.content.value;

    try {
      const response = await fetch('http://127.0.0.1:5000/api/wiadomosci', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ content }),
        credentials: 'include'
      });

      if (response.ok) {
        console.log('Wiadomość została wysłana');
        e.target.reset();
        // Możesz dodać dalszą obsługę, np. odświeżenie listy wiadomości
      } else {
        console.error('Błąd podczas wysyłania wiadomości');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h2>{user.role === 'admin' ? 'Wszystkie Wiadomości' : 'Moje Wiadomości'}</h2>
      {user.role !== 'admin' && (
        <form onSubmit={handleSubmit}>
          <textarea
            name="content"
            placeholder="Wiadomość"
          />
          <button type="submit">Wyślij</button>
        </form>
      )}
      <ul>
        {wiadomosci.map(wiadomosc => (
          <li key={wiadomosc.id}>
            <p>Od: {wiadomosc.sender}</p>
            <p>{wiadomosc.content}</p>
            <p>{wiadomosc.timestamp}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StronaWiadomosci;
