import React, { useState } from "react";
import "./stopka.css";

export default function Footer() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSuccessMessage("");
    setErrorMessage("");

    try {
      const response = await fetch("http://127.0.0.1:5000/api/wiadomosci", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
        credentials: "include", // Jeśli API wymaga ciasteczek
      });

      if (response.ok) {
        const data = await response.json();
        setSuccessMessage("Dziękujemy za Twoją wiadomość!");
        setFormData({ name: "", email: "", message: "" }); // Resetowanie formularza
      } else {
        const errorData = await response.json();
        setErrorMessage(
          errorData.message || "Wystąpił błąd podczas wysyłania wiadomości."
        );
      }
    } catch (error) {
      setErrorMessage("Nie udało się połączyć z serwerem.");
      console.error("Error:", error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="footer">
      <div className="footer-info">
        <div className="footer-message">
          <p className="masz-pytania-mia-opisz">Masz pytania? Śmiało pisz!</p>
          <p className="odpowiemy-na-ka-de-pytan">
            Odpowiemy na każde pytanie odnośnie usługi. Nie krępuj się, po prostu pisz!
          </p>
        </div>
        <form className="contact-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Imię i Nazwisko</label>
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Wpisz swoje imię i nazwisko"
              value={formData.name}
              onChange={handleChange}
              required
              disabled={isSubmitting}
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Wpisz swój email"
              value={formData.email}
              onChange={handleChange}
              required
              disabled={isSubmitting}
            />
          </div>
          <div className="form-group">
            <label htmlFor="message">Wiadomość</label>
            <textarea
              id="message"
              name="message"
              placeholder="Wpisz swoją wiadomość"
              value={formData.message}
              onChange={handleChange}
              required
              disabled={isSubmitting}
            />
          </div>
          <button type="submit" className="submit-button" disabled={isSubmitting}>
            {isSubmitting ? "Wysyłanie..." : "Zatwierdź"}
          </button>
        </form>
        {successMessage && <p className="success-message">{successMessage}</p>}
        {errorMessage && <p className="error-message">{errorMessage}</p>}
      </div>
    </div>
  );
}
