import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import './StronaAutoryzacji.css';
import axios from 'axios'; // Import Axios do zapytań API

const StronaAutoryzacji = () => {
  const { login, register } = useContext(AuthContext);
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [usernameAvailable, setUsernameAvailable] = useState(null); // Czy nazwa użytkownika jest dostępna
  const [usernameFeedback, setUsernameFeedback] = useState(''); // Komunikat o dostępności
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [feedback, setFeedback] = useState({ message: '', type: '' });

  useEffect(() => {
    document.title = isLogin ? 'Logowanie | Braid Hair Glamour' : 'Rejestracja | Braid Hair Glamour';
  }, [isLogin]);

  // Sprawdzenie dostępności nazwy użytkownika
  useEffect(() => {
    if (!isLogin && username.trim()) {
      const checkUsername = async () => {
        try {
          const response = await axios.get(`/api/sprawdz_nazwe_uzytkownika`, {
            params: { username },
          });
          setUsernameAvailable(response.data.available);
          setUsernameFeedback(response.data.message);
        } catch (err) {
          setUsernameAvailable(false);
          setUsernameFeedback('Nie udało się sprawdzić nazwy użytkownika.');
        }
      };
      checkUsername();
    }
  }, [username, isLogin]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFeedback({ message: '', type: '' });

    if (!usernameAvailable) {
      setFeedback({ message: 'Nazwa użytkownika jest zajęta.', type: 'error' });
      return;
    }

    try {
      if (isLogin) {
        const response = await login(username, password);
        setFeedback({ message: response.message || 'Zalogowano pomyślnie', type: 'success' });
      } else {
        const response = await register(username, email, password);
        setFeedback({ message: response.message || 'Rejestracja zakończona sukcesem', type: 'success' });
      }
    } catch (err) {
      setFeedback({ message: err.response?.data?.message || 'Wystąpił błąd. Spróbuj ponownie.', type: 'error' });
    }
  };

  return (
    <div className="auth-container">
      <h2>{isLogin ? 'Logowanie' : 'Rejestracja'}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Nazwa użytkownika"
        />
        {!isLogin && (
          <div className="username-feedback">
            {usernameFeedback && (
              <span className={usernameAvailable ? 'available' : 'unavailable'}>
                {usernameFeedback}
              </span>
            )}
          </div>
        )}
        {!isLogin && (
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Adres e-mail"
          />
        )}
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Hasło"
        />
        <button type="submit">{isLogin ? 'Zaloguj się' : 'Zarejestruj się'}</button>
      </form>

      {/* Komunikat o błędzie lub sukcesie */}
      {feedback.message && (
        <div className={`feedback ${feedback.type}`}>
          {feedback.message}
        </div>
      )}

      <button onClick={() => setIsLogin(!isLogin)}>
        {isLogin ? 'Nie masz konta? Zarejestruj się' : 'Masz już konto? Zaloguj się'}
      </button>
    </div>
  );
};

export default StronaAutoryzacji;
