import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import './StronaAutoryzacji.css';
import axios from 'axios'; // Import Axios do zapytań API
import { FaCheck, FaTimes } from 'react-icons/fa'; // Importujemy ikony ptaszka (check) i krzyżyka (times)

const StronaAutoryzacji = () => {
  const { login, register } = useContext(AuthContext);
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [usernameAvailable, setUsernameAvailable] = useState(null); 
  const [usernameFeedback, setUsernameFeedback] = useState(''); 
  const [email, setEmail] = useState('');
  const [emailAvailable, setEmailAvailable] = useState(null); // Stan do przechowywania dostępności e-maila
  const [emailFeedback, setEmailFeedback] = useState(''); // Komunikat zwrotny dla e-maila
  const [password, setPassword] = useState('');
  const [passwordFeedback, setPasswordFeedback] = useState(''); // Komunikat dla hasła
  const [passwordValid, setPasswordValid] = useState(false); // Stan walidacji hasła
  const [feedback, setFeedback] = useState({ message: '', type: '' });

  useEffect(() => {
    document.title = isLogin ? 'Logowanie | Braid Hair Glamour' : 'Rejestracja | Braid Hair Glamour';
  }, [isLogin]);

  useEffect(() => {
    if (!isLogin && username.trim()) {
      const checkUsername = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/api/sprawdz_nazwe_uzytkownika`, {
            params: { username },
          });
          setUsernameAvailable(response.data.available);
          setUsernameFeedback(response.data.message);
        } catch (err) {
          setUsernameAvailable(false);
          setUsernameFeedback('Nie udało się sprawdzić nazwy użytkownika.');
        }
      };
      checkUsername();
    }
  }, [username, isLogin]);

  useEffect(() => {
    if (!isLogin && email.trim()) {
      const checkEmail = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/api/sprawdz_email`, {
            params: { email },
          });
          setEmailAvailable(response.data.available);
          setEmailFeedback(response.data.message);
        } catch (err) {
          setEmailAvailable(false);
          setEmailFeedback('Nie udało się sprawdzić e-maila.');
        }
      };
      checkEmail();
    }
  }, [email, isLogin]);

  useEffect(() => {
    if (password) {
      // Prosta walidacja hasła (min 6 znaków)
      const isValid = password.length >= 6;
      setPasswordValid(isValid);
      setPasswordFeedback(isValid ? 'Hasło jest poprawne' : 'Hasło musi mieć co najmniej 6 znaków');
    }
  }, [password]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFeedback({ message: '', type: '' });

    if (!usernameAvailable || !emailAvailable || !passwordValid) {
      setFeedback({ message: 'Sprawdź poprawność danych.', type: 'error' });
      return;
    }

    try {
      if (isLogin) {
        const response = await login(username, password);
        setFeedback({ message: response.message || 'Zalogowano pomyślnie', type: 'success' });
      } else {
        const response = await register(username, email, password);
        setFeedback({ message: response.message || 'Rejestracja zakończona sukcesem', type: 'success' });
      }
    } catch (err) {
      setFeedback({ message: err.response?.data?.message || 'Wystąpił błąd. Spróbuj ponownie.', type: 'error' });
    }
  };

  return (
    <div className="auth-container">
      <h2>{isLogin ? 'Logowanie' : 'Rejestracja'}</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Nazwa użytkownika"
          />
          <div className="status-icon">
            {usernameAvailable === null ? null : usernameAvailable ? <FaCheck className="valid" /> : <FaTimes className="invalid" />}
          </div>
        </div>

        {!isLogin && (
          <div className="input-group">
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Adres e-mail"
            />
            <div className="status-icon">
              {emailAvailable === null ? null : emailAvailable ? <FaCheck className="valid" /> : <FaTimes className="invalid" />}
            </div>
          </div>
        )}

        <div className="input-group">
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Hasło"
          />
          <div className="status-icon">
            {passwordValid === null ? null : passwordValid ? <FaCheck className="valid" /> : <FaTimes className="invalid" />}
          </div>
        </div>

        <button type="submit">{isLogin ? 'Zaloguj się' : 'Zarejestruj się'}</button>
      </form>

      {/* Komunikat o błędzie lub sukcesie */}
      {feedback.message && (
        <div className={`feedback ${feedback.type}`}>
          {feedback.message}
        </div>
      )}

      <button onClick={() => setIsLogin(!isLogin)}>
        {isLogin ? 'Nie masz konta? Zarejestruj się' : 'Masz już konto? Zaloguj się'}
      </button>
    </div>
  );
};

export default StronaAutoryzacji;
