import React, { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import axios from 'axios';

const StronaWiadomosci = () => {
  const { user } = useContext(AuthContext);
  const [wiadomosci, setWiadomosci] = useState({ received: [], sent: [] });
  const [content, setContent] = useState('');
  const [receiverId, setReceiverId] = useState('');

  useEffect(() => {
    const pobierzWiadomosci = async () => {
      const response = await axios.get('/api/wiadomosci');
      setWiadomosci(response.data);
    };
    pobierzWiadomosci();
  }, []);

  const wyslijWiadomosc = async (e) => {
    e.preventDefault();
    await axios.post('/api/wiadomosci', { receiver_id: receiverId, content });
    setContent('');
    setReceiverId('');
    const response = await axios.get('/api/wiadomosci');
    setWiadomosci(response.data);
  };

  return (
    <div>
      <h2>Wiadomości</h2>
      <form onSubmit={wyslijWiadomosc}>
        <input
          type="text"
          placeholder="ID odbiorcy"
          value={receiverId}
          onChange={(e) => setReceiverId(e.target.value)}
        />
        <textarea
          placeholder="Treść wiadomości"
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
        <button type="submit">Wyślij</button>
      </form>
      <div>
        <h3>Otrzymane wiadomości</h3>
        {wiadomosci.received.map((msg) => (
          <div key={msg.id}>
            <p><strong>Od:</strong> {msg.sender}</p>
            <p>{msg.content}</p>
            <p><small>{msg.timestamp}</small></p>
          </div>
        ))}
      </div>
      <div>
        <h3>Wysłane wiadomości</h3>
        {wiadomosci.sent.map((msg) => (
          <div key={msg.id}>
            <p><strong>Do:</strong> {msg.receiver}</p>
            <p>{msg.content}</p>
            <p><small>{msg.timestamp}</small></p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default StronaWiadomosci;
