import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import './StronaAutoryzacji.css';
import axios from 'axios';

const StronaAutoryzacji = () => {
  const { login, register } = useContext(AuthContext);
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [usernameAvailable, setUsernameAvailable] = useState(null);
  const [usernameFeedback, setUsernameFeedback] = useState('');
  const [email, setEmail] = useState('');
  const [emailAvailable, setEmailAvailable] = useState(null);
  const [emailFeedback, setEmailFeedback] = useState('');
  const [password, setPassword] = useState('');
  const [passwordValid, setPasswordValid] = useState(null);
  const [passwordFeedback, setPasswordFeedback] = useState('');
  const [feedback, setFeedback] = useState({ message: '', type: '' });

  useEffect(() => {
    document.title = isLogin ? 'Logowanie | Braid Hair Glamour' : 'Rejestracja | Braid Hair Glamour';
  }, [isLogin]);

  // Sprawdzenie dostępności nazwy użytkownika
  useEffect(() => {
    if (!isLogin && username.trim()) {
      const checkUsername = async () => {
        try {
          const response = await axios.get('http://localhost:5000/api/sprawdz_nazwe_uzytkownika', {
            params: { username },
          });
          setUsernameAvailable(response.data.available);
          setUsernameFeedback(response.data.message);
        } catch (err) {
          setUsernameAvailable(false);
          setUsernameFeedback('Nie udało się sprawdzić nazwy użytkownika.');
        }
      };
      checkUsername();
    }
  }, [username, isLogin]);

  // Sprawdzenie dostępności adresu email
  useEffect(() => {
    if (!isLogin && email.trim()) {
      const checkEmail = async () => {
        try {
          const response = await axios.get('http://localhost:5000/api/sprawdz_email', {
            params: { email },
          });
          setEmailAvailable(response.data.available);
          setEmailFeedback(response.data.message);
        } catch (err) {
          setEmailAvailable(false);
          setEmailFeedback('Nie udało się sprawdzić adresu e-mail.');
        }
      };
      checkEmail();
    }
  }, [email, isLogin]);

  // Walidacja hasła
  useEffect(() => {
    if (password) {
      const passwordRegex = /^(?=.*[A-Za-z])(?=.*\d)(?=.*[!@#$%^&*])[A-Za-z\d!@#$%^&*]{8,}$/; // minimalna długość 8, zawiera literę, cyfrę, i specjalny znak
      setPasswordValid(passwordRegex.test(password));
      setPasswordFeedback(passwordValid ? 'Hasło jest poprawne' : 'Hasło musi zawierać co najmniej 8 znaków, w tym literę, cyfrę i znak specjalny');
    }
  }, [password]);

  // Obsługa formularza
  const handleSubmit = async (e) => {
    e.preventDefault();
    setFeedback({ message: '', type: '' });

    if (!usernameAvailable) {
      setFeedback({ message: 'Nazwa użytkownika jest zajęta.', type: 'error' });
      return;
    }

    if (!emailAvailable) {
      setFeedback({ message: 'E-mail jest już zajęty.', type: 'error' });
      return;
    }

    if (!passwordValid) {
      setFeedback({ message: 'Hasło jest nieprawidłowe.', type: 'error' });
      return;
    }

    try {
      if (isLogin) {
        const response = await login(username, password);
        setFeedback({ message: response.message || 'Zalogowano pomyślnie', type: 'success' });
      } else {
        const response = await register(username, email, password);
        setFeedback({ message: response.message || 'Rejestracja zakończona sukcesem', type: 'success' });
      }
    } catch (err) {
      setFeedback({ message: err.response?.data?.message || 'Wystąpił błąd. Spróbuj ponownie.', type: 'error' });
    }
  };

  return (
    <div className="auth-container">
      <h2>{isLogin ? 'Logowanie' : 'Rejestracja'}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Nazwa użytkownika"
        />
        {!isLogin && (
          <div className="username-feedback">
            {usernameFeedback && (
              <span className={usernameAvailable ? 'available' : 'unavailable'}>
                {usernameFeedback}
              </span>
            )}
          </div>
        )}

        {!isLogin && (
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Adres e-mail"
          />
        )}
        {!isLogin && email && (
          <div className="email-feedback">
            {emailFeedback && (
              <span className={emailAvailable ? 'available' : 'unavailable'}>
                {emailFeedback}
              </span>
            )}
          </div>
        )}

        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Hasło"
        />
        {password && (
          <div className="password-feedback">
            {passwordFeedback && (
              <span className={passwordValid ? 'available' : 'unavailable'}>
                {passwordFeedback}
              </span>
            )}
          </div>
        )}

        <button type="submit">{isLogin ? 'Zaloguj się' : 'Zarejestruj się'}</button>
      </form>

      {/* Komunikat o błędzie lub sukcesie */}
      {feedback.message && (
        <div className={`feedback ${feedback.type}`}>
          {feedback.message}
        </div>
      )}

      <button onClick={() => setIsLogin(!isLogin)}>
        {isLogin ? 'Nie masz konta? Zarejestruj się' : 'Masz już konto? Zaloguj się'}
      </button>
    </div>
  );
};

export default StronaAutoryzacji;
