import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import './StronaRezerwacji.css';

const StronaRezerwacji = () => {
  const { user } = useContext(AuthContext);
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    if (user) {
      fetch('http://127.0.0.1:5000/api/rezerwacje', {
        credentials: 'include'
      })
        .then(response => response.json())
        .then(data => setBookings(data.bookings))
        .catch(error => console.error('Error:', error));
    }
  }, [user]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://127.0.0.1:5000/api/rezerwacje', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ date, time }),
        credentials: 'include'
      });

      if (response.ok) {
        const data = await response.json();
        setBookings([...bookings, data]);
      } else {
        console.error('Błąd podczas tworzenia rezerwacji');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleUpdate = async (id) => {
    const newDate = prompt('Podaj nową datę:');
    const newTime = prompt('Podaj nową godzinę:');

    try {
      const response = await fetch(`http://127.0.0.1:5000/api/rezerwacje/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ date: newDate, time: newTime }),
        credentials: 'include'
      });

      if (response.ok) {
        setBookings(bookings.map(booking => (booking.id === id ? { ...booking, date: newDate, time: newTime } : booking)));
      } else {
        console.error('Błąd podczas aktualizacji rezerwacji');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/api/rezerwacje/${id}`, {
        method: 'DELETE',
        credentials: 'include'
      });

      if (response.ok) {
        setBookings(bookings.filter(booking => booking.id !== id));
      } else {
        console.error('Błąd podczas usuwania rezerwacji');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="rezerwacja-container">
      <h2>{user.role === 'admin' ? 'Lista Rezerwacji' : 'Zarezerwuj Wizytę'}</h2>
      {user.role !== 'admin' && (
        <form onSubmit={handleSubmit}>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            placeholder="Data"
          />
          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            placeholder="Godzina"
          />
          <button type="submit">Zarezerwuj</button>
        </form>
      )}
      <ul>
        {bookings.map(booking => (
          <li key={booking.id}>
            <p>Data: {booking.date}</p>
            <p>Godzina: {booking.time}</p>
            <p>Użytkownik: {booking.user_id}</p>
            {user.role !== 'admin' && (
              <>
                <button onClick={() => handleUpdate(booking.id)}>Aktualizuj</button>
                <button onClick={() => handleDelete(booking.id)}>Usuń</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StronaRezerwacji;
