import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import './StronaAutoryzacji.css';

const StronaAutoryzacji = () => {
  const { login, register } = useContext(AuthContext);
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(''); // Stan przechowujący błędy

  useEffect(() => {
    document.title = isLogin ? 'Logowanie | Braid Hair Glamour' : 'Rejestracja | Braid Hair Glamour';
  }, [isLogin]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Resetowanie błędów przed nową próbą

    try {
      if (isLogin) {
        // Wysyłanie danych logowania
        const response = await login(username, password);
        if (response.message) {
          setError(response.message); // Ustawienie komunikatu błędu
        }
      } else {
        // Wysyłanie danych rejestracji
        const response = await register(username, password);
        if (response.message) {
          setError(response.message); // Ustawienie komunikatu błędu
        }
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Wystąpił błąd. Spróbuj ponownie.'); // Obsługa błędu z API
    }
  };

  return (
    <div className="auth-container">
      <h2>{isLogin ? 'Logowanie' : 'Rejestracja'}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Nazwa użytkownika"
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Hasło"
        />
        <button type="submit">{isLogin ? 'Zaloguj się' : 'Zarejestruj się'}</button>
      </form>
      {error && <div className="error-message">{error}</div>} {/* Wyświetlanie komunikatu błędu */}
      <button onClick={() => setIsLogin(!isLogin)}>
        {isLogin ? 'Nie masz konta? Zarejestruj się' : 'Masz już konto? Zaloguj się'}
      </button>
    </div>
  );
};

export default StronaAutoryzacji;
