import React, { useContext, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import './StronaRezerwacji.css';

const StronaRezerwacji = () => {
  const { user } = useContext(AuthContext);
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://127.0.0.1:5000/api/rezerwacje', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ date, time }),
      });

      if (response.ok) {
        console.log('Rezerwacja została utworzona');
        // Możesz dodać dalszą obsługę, np. wyczyszczenie formularza lub przekierowanie
      } else {
        console.error('Błąd podczas tworzenia rezerwacji');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="rezerwacja-container">
      <h2>Zarezerwuj Wizytę</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={user?.username || ''}
          disabled
          placeholder="Nazwa użytkownika"
        />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          placeholder="Data"
        />
        <input
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          placeholder="Godzina"
        />
        <button type="submit">Zarezerwuj</button>
      </form>
    </div>
  );
};

export default StronaRezerwacji;
