import React, { useState } from "react";
import "./stopka.css";

export default function Footer() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form data submitted:", formData);
    // Tutaj możesz dodać kod do wysyłki danych na backend lub API.
    alert("Dziękujemy za Twoją wiadomość!");
  };

  return (
    <div className="footer">
      <div className="footer-info">
        <div className="footer-message">
          <p className="masz-pytania-mia-opisz">Masz pytania? Śmiało pisz!</p>
          <p className="odpowiemy-na-ka-de-pytan">
            Odpowiemy na każde pytanie odnośnie usługi. Nie krępuj się, po prostu pisz!
          </p>
        </div>
        <form className="contact-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Imię i Nazwisko</label>
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Wpisz swoje imię i nazwisko"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Wpisz swój email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="message">Wiadomość</label>
            <textarea
              id="message"
              name="message"
              placeholder="Wpisz swoją wiadomość"
              value={formData.message}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="submit-button">
            Zatwierdź
          </button>
        </form>
      </div>
    </div>
  );
}
