import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import './StronaRezerwacji.css';

const StronaRezerwacji = () => {
  const { user } = useContext(AuthContext);
  const [rezerwacje, setRezerwacje] = useState([]);
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');

  useEffect(() => {
    // Fetch reservations for the logged-in user
    fetch(`/api/rezerwacje?user=${user.id}`)
      .then(response => response.json())
      .then(data => setRezerwacje(data))
      .catch(error => console.error('Error:', error));
  }, [user]);

  const handleReservation = (e) => {
    e.preventDefault();
    // Post reservation
    fetch('/api/rezerwacje', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ date, time }),
    })
      .then(response => response.json())
      .then(data => setRezerwacje([...rezerwacje, data]))
      .catch(error => console.error('Error:', error));
  };

  return (
    <div className="reservation-container">
      <h2>Zarezerwuj wizytÄ™</h2>
      <form onSubmit={handleReservation}>
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
        <input
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
        />
        <button type="submit">Zarezerwuj</button>
      </form>
      <ul className="reservation-list">
        {rezerwacje.map(rezerwacja => (
          <li key={rezerwacja.id}>
            {rezerwacja.date} {rezerwacja.time}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StronaRezerwacji;
