import React, { useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext"; // Załóżmy, że jest dostępny AuthContext
import "./stopka.css";

export default function Footer() {
  const { user } = useContext(AuthContext); // Obsługa użytkownika
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });
  const [feedback, setFeedback] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFeedback(""); // Czyścimy poprzednie wiadomości

    try {
      const response = await fetch("http://127.0.0.1:5000/api/wiadomosci", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name: formData.name,
          email: formData.email,
          content: formData.message, // Używamy "content", zgodnie z API
        }),
        credentials: "include",
      });

      if (response.ok) {
        setFeedback("Wiadomość została wysłana pomyślnie!");
        setFormData({ name: "", email: "", message: "" }); // Reset formularza
      } else {
        const errorData = await response.json();
        setFeedback(errorData.error || "Wystąpił błąd podczas wysyłania wiadomości.");
      }
    } catch (error) {
      console.error("Error:", error);
      setFeedback("Wystąpił błąd sieci. Spróbuj ponownie później.");
    }
  };

  return (
    <div className="footer">
      <div className="footer-info">
        <div className="footer-message">
          <p className="masz-pytania-mia-opisz">Masz pytania? Śmiało pisz!</p>
          <p className="odpowiemy-na-ka-de-pytan">
            Odpowiemy na każde pytanie odnośnie usługi. Nie krępuj się, po prostu pisz!
          </p>
        </div>
        <form className="contact-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Imię i Nazwisko</label>
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Wpisz swoje imię i nazwisko"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Wpisz swój email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="message">Wiadomość</label>
            <textarea
              id="message"
              name="message"
              placeholder="Wpisz swoją wiadomość"
              value={formData.message}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="submit-button">
            Zatwierdź
          </button>
        </form>
        {feedback && <p className="feedback">{feedback}</p>}
      </div>
    </div>
  );
}
