import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';

const StronaWiadomosci = () => {
  const { user } = useContext(AuthContext);
  const [wiadomosci, setWiadomosci] = useState([]);

  useEffect(() => {
    const fetchWiadomosci = async () => {
      const response = await fetch(`/api/wiadomosci`);
      if (response.ok) {
        const data = await response.json();
        setWiadomosci(data);
      }
    };
    fetchWiadomosci();
  }, [user]);

  return (
    <div>
      <h2>Moje Wiadomości</h2>
      <ul>
        {wiadomosci.received && wiadomosci.received.map(wiadomosc => (
          <li key={wiadomosc.id}>
            Od: {wiadomosc.sender}, Treść: {wiadomosc.content}, Data: {wiadomosc.timestamp}
          </li>
        ))}
        {wiadomosci.sent && wiadomosci.sent.map(wiadomosc => (
          <li key={wiadomosc.id}>
            Do: {wiadomosc.receiver}, Treść: {wiadomosc.content}, Data: {wiadomosc.timestamp}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StronaWiadomosci;
